package Ch03_Operator;

public class xor_practice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 15;
		int b = 5;
		System.out.println(a | b);
		System.out.println(a & b);
		System.out.println(a ^ b);

		System.out.println(a >> 2);
		System.out.println(b << 4);

	}

}
