package Operator;

public class operator_ch3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 1;
		System.out.println(a);

		a++;
		System.out.println(a);
		System.out.println(++a);
		System.out.println(a++);
		System.out.println(a);

	}

}
